import React from 'react';
import styled from 'styled-components';
import { COLORS } from '../../util/constants';

type Props = {
  title: string;
  color?: string;
  disabled?: boolean;
  background?: string;
  onClick: () => void;
};

const ButtonRoot = styled.button<Props>`
  display: flex;
  align-items: center;
  background: ${(props: Props) => props.background || COLORS.PRIMARY};
  color: ${(props: Props) => props.color || 'white'};
  font-weight: 600;
  font-size: 13px;
  height: 30px;
  cursor: pointer;
  border-radius: 4px;
  outline: 0;
  & + & {
    margin-left: 10px;
  }
`;

const Button: React.FC<Props> = (props: Props) => {
  return <ButtonRoot {...props}>{props.title}</ButtonRoot>;
};

export default Button;
