import React from 'react';
import styled from 'styled-components';
import { COLORS } from '../../util/constants';
import Button from '../shared/Button';
import Icon from '../shared/Icon';

const BannerRoot = styled.div`
  display: flex;
  height: auto;
  width: 100%;
  padding: 20px;
  flex-direction: column;
  justify-content: space-evenly;
`;

const LeftSectionText = styled.h1`
  font-size: 5vw;
  font-weight: bold;
`;

const RightSectionText = styled.div`
  font-weight: bold;
  font-size: 10vw;
  color: #607d8b;
`;

const AddSection = styled.div`
  display: flex;
  justify-content: space-evenly;
`;

const JoinUsSection = styled.div`
  margin-top: 40px;
  display: flex;
  justify-content: center;
`;

const JoinUsButton = styled(Button)`
  height: 40px;
  width: 130px;
  font-size: 15px;
  margin-top: 20px;
`;

const Banner = () => {
  return (
    <BannerRoot>
      <AddSection>
        <LeftSectionText>Introducing</LeftSectionText>
        <Icon name="story" size={100} />
        <RightSectionText>Virtuoso</RightSectionText>
      </AddSection>
      <JoinUsSection>
        <JoinUsButton title="Join Us" onClick={() => {}} icon="tick" />
      </JoinUsSection>
    </BannerRoot>
  );
};

export default Banner;
