import React from 'react';
import styled from 'styled-components';
import { COLORS } from '../../util/constants';
import Button from '../shared/Button';

const BannerRoot = styled.div`
  display: flex;
  height: 200px;
  width: 100%;
  padding: 20px;
  flex-direction: column;
  justify-content: space-evenly;
  border-bottom: 2px solid #eceff1;
`;

const LeftSectionText = styled.h1`
  font-size: 5vw;
  font-weight: bold;
`;

const RightSectionText = styled.div`
  font-weight: bold;
  font-size: 10vw;
  color: ${COLORS.PRIMARY};
`;

const AddSection = styled.div`
  display: flex;
  justify-content: space-evenly;
`;

const JoinUsSection = styled.div``;

const Banner = () => {
  return (
    <BannerRoot>
      <AddSection>
        <LeftSectionText>Introducing</LeftSectionText>
        <RightSectionText>Virtuoso</RightSectionText>
      </AddSection>
      <JoinUsSection>
        <Button title="Join Us" onClick={() => {}} />
      </JoinUsSection>
    </BannerRoot>
  );
};

export default Banner;
